import React, { useState } from 'react';
import { Upload, Table, Card, Button, Spin, Alert, Typography } from 'antd';
import { UploadOutlined, GiftOutlined, DownloadOutlined } from '@ant-design/icons';
import Papa from 'papaparse';

const { Title } = Typography;

const SecretSantaApp = () => {
  const [secretSantaAssignments, setSecretSantaAssignments] = useState(null);
  const [error, setError] = useState(null);
  const [loading, setLoading] = useState(false);

  const handleFileUpload = async (info) => {
    if (info.file.status !== 'uploading') {
      setLoading(true);
      setError(null);

      try {
        Papa.parse(info.file.originFileObj, {
          header: true,
          complete: async (results) => {
            try {
              const formData = new FormData();
              formData.append('csvFile', info.file.originFileObj);

              const response = await fetch('http://localhost:5001/api/secret_santa', {
                method: 'POST',
                body: formData,
              });

              if (!response.ok) {
                throw new Error('Failed to generate assignments');
              }

              const data = await response.json();
              setSecretSantaAssignments(data.assignments);
              info.file.status = 'done';
            } catch (err) {
              setError(err.message);
              info.file.status = 'error';
            } finally {
              setLoading(false);
            }
          },
          error: (err) => {
            setError('Failed to parse CSV file');
            setLoading(false);
            info.file.status = 'error';
          },
        });
      } catch (err) {
        setError(err.message);
        setLoading(false);
        info.file.status = 'error';
      }
    }
  };

  const downloadAssignments = () => {
    if (!secretSantaAssignments) return;

    const csv = Papa.unparse(secretSantaAssignments);
    const blob = new Blob([csv], { type: 'text/csv' });
    const url = window.URL.createObjectURL(blob);
    const a = document.createElement('a');
    a.href = url;
    a.download = 'secret_santa_assignments.csv';
    document.body.appendChild(a);
    a.click();
    document.body.removeChild(a);
    window.URL.revokeObjectURL(url);
  };

  const columns = [
    {
      title: 'Santa',
      key: 'santa',
      render: (text, record) => (
        <div>
          <div className="font-medium">{record.Employee_Name}</div>
          <div className="text-gray-500">{record.Employee_EmailID}</div>
        </div>
      ),
    },
    {
      title: 'Gift Recipient',
      key: 'recipient',
      render: (text, record) => (
        <div>
          <div className="font-medium">{record.Secret_Child_Name}</div>
          <div className="text-gray-500">{record.Secret_Child_EmailID}</div>
        </div>
      ),
    },
  ];

  return (
    <div>
      <Card>
        <div className="text-center mb-6">
          <Title level={2} className="flex items-center justify-center gap-2">
            <GiftOutlined />
            Secret Santa Assignment Generator
          </Title>
        </div>

        <Upload.Dragger
          name="file"
          accept=".csv"
          customRequest={({ file, onSuccess }) => {
            handleFileUpload({ file: { originFileObj: file } });
            onSuccess();
          }}
          showUploadList={false}
          className="mb-8"
        >
          <p className="ant-upload-drag-icon">
            <UploadOutlined className="text-4xl" />
          </p>
          <p className="ant-upload-text">Upload your CSV file with employee data</p>
          <p className="ant-upload-hint">
            File should include Employee_Name and Employee_EmailID columns
          </p>
        </Upload.Dragger>

        {loading && (
          <div className="text-center py-4">
            <Spin size="large" />
            <p className="mt-2">Generating assignments...</p>
          </div>
        )}

        {error && (
          <Alert
            message="Error"
            description={error}
            type="error"
            showIcon
            className="mb-4"
          />
        )}

        {secretSantaAssignments && (
          <div className="mt-8">
            <div className="flex justify-between items-center mb-4">
              <Title level={3}>Secret Santa Assignments</Title>
              <Button
                type="primary"
                icon={<DownloadOutlined />}
                onClick={downloadAssignments}
              >
                Download CSV
              </Button>
            </div>
            <Table
              columns={columns}
              dataSource={secretSantaAssignments}
              rowKey={(record) => record.Employee_EmailID}
              pagination={false}
            />
          </div>
        )}
      </Card>
    </div>
  );
};

export default SecretSantaApp;
