import React, { useState } from 'react';
import Papa from 'papaparse';
import { FileUp, Gift, Download } from 'lucide-react';
import { Alert, AlertDescription } from './components/ui/alert';
import { Card, CardContent, CardHeader, CardTitle } from './components/ui/card';

const SecretSantaApp = () => {
  const [secretSantaAssignments, setSecretSantaAssignments] = useState(null);
  const [error, setError] = useState(null);
  const [loading, setLoading] = useState(false);

  const handleFileUpload = async (event) => {
    const file = event.target.files[0];
    if (!file) return;

    setLoading(true);
    setError(null);

    try {
      // Parse CSV file
      Papa.parse(file, {
        header: true,
        complete: async (results) => {
          try {
            const formData = new FormData();
            formData.append('csvFile', file);

            const response = await fetch('http://localhost:5001/api/secret_santa', {
              method: 'POST',
              body: formData,
            });

            if (!response.ok) {
              throw new Error('Failed to generate assignments');
            }

            const data = await response.json();
            setSecretSantaAssignments(data.assignments);
          } catch (err) {
            setError(err.message);
          } finally {
            setLoading(false);
          }
        },
        error: (err) => {
          setError('Failed to parse CSV file');
          setLoading(false);
        },
      });
    } catch (err) {
      setError(err.message);
      setLoading(false);
    }
  };

  const downloadAssignments = () => {
    if (!secretSantaAssignments) return;

    const csv = Papa.unparse(secretSantaAssignments);
    const blob = new Blob([csv], { type: 'text/csv' });
    const url = window.URL.createObjectURL(blob);
    const a = document.createElement('a');
    a.href = url;
    a.download = 'secret_santa_assignments.csv';
    document.body.appendChild(a);
    a.click();
    document.body.removeChild(a);
    window.URL.revokeObjectURL(url);
  };

  return (
    <div className="min-h-screen bg-gray-50 p-8">
      <Card className="max-w-4xl mx-auto">
        <CardHeader>
          <CardTitle className="text-2xl font-bold text-center flex items-center justify-center gap-2">
            <Gift className="w-6 h-6" />
            Secret Santa Assignment Generator
          </CardTitle>
        </CardHeader>
        <CardContent>
          {/* File Upload Section */}
          <div className="mb-8">
            <label
              className="block w-full border-2 border-dashed border-gray-300 rounded-lg p-8 text-center cursor-pointer hover:border-blue-500 transition-colors"
            >
              <input
                type="file"
                accept=".csv"
                onChange={handleFileUpload}
                className="hidden"
              />
              <FileUp className="w-12 h-12 mx-auto mb-4 text-gray-400" />
              <p className="text-sm text-gray-600">
                Upload your CSV file with employee data
              </p>
              <p className="text-xs text-gray-500 mt-2">
                File should include Employee_Name and Employee_EmailID columns
              </p>
            </label>
          </div>

          {/* Loading State */}
          {loading && (
            <div className="text-center py-4">
              <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-gray-900 mx-auto"></div>
              <p className="mt-2 text-sm text-gray-600">Generating assignments...</p>
            </div>
          )}

          {/* Error Message */}
          {error && (
            <Alert variant="destructive" className="mb-4">
              <AlertDescription>{error}</AlertDescription>
            </Alert>
          )}

          {/* Assignments Display */}
          {secretSantaAssignments && (
            <div className="mt-8">
              <div className="flex justify-between items-center mb-4">
                <h2 className="text-xl font-semibold">Secret Santa Assignments</h2>
                <button
                  onClick={downloadAssignments}
                  className="flex items-center gap-2 bg-blue-500 text-white px-4 py-2 rounded-lg hover:bg-blue-600 transition-colors"
                >
                  <Download className="w-4 h-4" />
                  Download CSV
                </button>
              </div>
              <div className="bg-white rounded-lg shadow overflow-hidden">
                <table className="min-w-full divide-y divide-gray-200">
                  <thead className="bg-gray-50">
                    <tr>
                      <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                        Santa
                      </th>
                      <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                        Gift Recipient
                      </th>
                    </tr>
                  </thead>
                  <tbody className="bg-white divide-y divide-gray-200">
                    {secretSantaAssignments.map((assignment, index) => (
                      <tr key={index} className="hover:bg-gray-50">
                        <td className="px-6 py-4 whitespace-nowrap">
                          <div className="text-sm font-medium text-gray-900">
                            {assignment.Employee_Name}
                          </div>
                          <div className="text-sm text-gray-500">
                            {assignment.Employee_EmailID}
                          </div>
                        </td>
                        <td className="px-6 py-4 whitespace-nowrap">
                          <div className="text-sm font-medium text-gray-900">
                            {assignment.Secret_Child_Name}
                          </div>
                          <div className="text-sm text-gray-500">
                            {assignment.Secret_Child_EmailID}
                          </div>
                        </td>
                      </tr>
                    ))}
                  </tbody>
                </table>
              </div>
            </div>
          )}
        </CardContent>
      </Card>
    </div>
  );
};

export default SecretSantaApp;
