import React, { useState } from 'react';
import Papa from 'papaparse';

function App() {
  const [secretSantaAssignments, setSecretSantaAssignments] = useState(null);

  const handleFileUpload = (event) => {
    const file = event.target.files[0];
    Papa.parse(file, {
      header: true,
      complete: async (results) => {
        const formData = new FormData();
        formData.append('csvFile', file);

        const response = await fetch('http://localhost:5001/api/secret_santa', {
          method: 'POST',
          body: formData,
        });
        const data = await response.json();
        setSecretSantaAssignments(data.assignments);
      },
    });
  };

  return (
    <div>
      <h1>Secret Santa App</h1>
      <input type="file" accept=".csv" onChange={handleFileUpload} />

      {secretSantaAssignments && (
        <div>
          <h2>Secret Santa Assignments:</h2>
          <ul>
            {secretSantaAssignments.map((assignment, index) => (
              <li key={index}>
                {assignment.santaName} will give a gift to {assignment.childName}
              </li>
            ))}
          </ul>
        </div>
      )}
    </div>
  );
}

export default App;
